import React, { createContext, useContext, useEffect, useState } from 'react';

// Create a context to hold theme state and toggle function
const ThemeContext = createContext();

// Custom hook to access the theme context
export const useTheme = () => {
  return useContext(ThemeContext);
};

// ThemeProvider component that provides theme state and toggle function to its children
export const ThemeProvider = ({ children }) => {
  const [isDarkTheme, setIsDarkTheme] = useState(false);

  // Toggle the theme between dark and light
  const toggleTheme = () => {
    setIsDarkTheme(prevTheme => !prevTheme);
  };

  // Apply the selected theme to the body class
  useEffect(() => {
    document.body.classList.toggle('dark', isDarkTheme);
  }, [isDarkTheme]);

  // Pass theme state and toggle function to context
  const contextValue = {
    isDarkTheme,
    toggleTheme,
  };

  return (
    <ThemeContext.Provider value={contextValue}>
      {children}
    </ThemeContext.Provider>
  );
};
